css:
  title {
     /*align-items: center; */
  }
  
  .schedule-container {
    font-family: Arial, sans-serif;
    font-size: 12px;
    padding: 20px;
  }

  .section-heading {
    background-color: #e74c3c;
    color: white;
    padding: 10px;
    margin-top: 30px;
    font-size: 14px;
    font-weight: bold;
    border-radius: 4px;
  }

  .drill-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 10px;
  }

  .drill-table th, .drill-table td {
    border: 1px solid #ccc;
    padding: 8px;
    vertical-align: top;
  }

  .drill-table th {
    background-color: #f8f9fa;
    font-weight: bold;
    text-align: left;
  }

  .month-col {
    width: 150px;
    font-weight: bold;
    color: #2c3e50;
  }

  .safety-col {
    color: #34495e;
  }
