css:
.flash-print-container {
  font-family: Arial, sans-serif;
  font-size: 11pt;
  color: #000;
  padding: 10px;
}

.print-section {
  border: 1px solid #aaa;
  margin-bottom: 25px;
  padding: 10px;
  page-break-inside: avoid;
}

.section-title {
  font-size: 16pt;
  font-weight: bold;
  color: #b40000;
  border-bottom: 2px solid #b40000;
  margin-bottom: 10px;
  padding-bottom: 4px;
}

.info-table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 10px;
}

.info-table th,
.info-table td {
  text-align: left;
  padding: 8px 10px;
  border: 1px solid #ccc;
  vertical-align: top;
}

.info-table th {
  background-color: #eaeaea;
  width: 35%;
  font-weight: bold;
  color: black;
}

.info-table tbody tr:nth-child(even) {
  background-color: #fff;
}

.info-table tbody tr:nth-child(odd) {
  background-color: #f9f9f9;
}

.photo-preview {
  margin-top: 5px;
  max-width: 300px;
  max-height: 300px;
  border: 1px solid #ddd;
  padding: 4px;
}
