css:
/* --- COVER PAGE STYLING --- */
.cover-page {
  margin: 0 auto;
  width: 90%;
  font-family: Arial, sans-serif;
}

.cover-title {
  font-size: 24pt;
  text-align: center;
  font-weight: bold;
  margin-bottom: 30px;
  color: #b40000;
  text-transform: uppercase;
}

.cover-section-title {
  font-size: 14pt;
  font-weight: bold;
  margin: 30px 0 10px;
  color: #b40000;
  border-bottom: 2px solid #c00;
  padding-bottom: 4px;
}

/* Shared table styling for all three cover page tables */
.cover-info-table,
.approval-table,
.history-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;
  font-size: 11pt;
  background-color: #fff;
}

.cover-info-table th,
.cover-info-table td,
.approval-table th,
.approval-table td,
.history-table th,
.history-table td {
  border: 1px solid #ddd;
  padding: 6px 10px;
}

/* Headers: black text */
.cover-info-table th,
.approval-table th,
.history-table th {
  color: #000;                /* black text for headers */
  font-weight: bold;
  text-align: left;
}

/* Zebra striping (slight red tint) */
.cover-info-table tr:nth-child(odd),
.approval-table tbody tr:nth-child(odd),
.history-table tbody tr:nth-child(odd) {
  background-color: #EAE9E9;
}

.cover-info-table tr:nth-child(even),
.approval-table tbody tr:nth-child(even),
.history-table tbody tr:nth-child(even) {
  background-color: #fff;
}


.form {
  padding: 20px;
  margin-bottom: 30px;
  background-color: #fff;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.3); /* subtle 3D shadow */
  border-radius: 4px; /* optional for rounded corners */
}



.main-title {
  font-size: 20pt;
  margin-bottom: 30px;
  margin-top: 5px;
  text-align: center;
}

.print-heading {
  text-align: center;
  margin-bottom: 20px;
  padding-bottom: 5px;
  border-bottom: 3px solid #c00;
}

.print-heading h1 {
  margin: 0;
  font-size: 18pt;
  font-family: Didot;
}

.kpi-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;
  border: 1px solid #ddd;
}

.kpi-table th,
.kpi-table td {
  padding: 6px;
  border-bottom: 1px solid #b40000;
  border-right: 1px solid #b40000;
  word-wrap: break-word;
  hyphens: auto;
}

.kpi-table th {
  background-color: #EAE9E9;
  font-weight: 600;
  color: black;
}

.kpi-table td {
    color: black;
}

.kpi-table td:last-child,
.kpi-table th:last-child {
  border-right: none;
}

.kpi-table tr:last-child td {
  border-bottom: none;
}

.kpi-table tbody tr:nth-child(odd) {
  background-color: #EAE9E9;
}



.page-footer {
  margin-top: 10px;
  font-size: 12pt;
}

.footer-table {
  width: 100%;
  border-collapse: collapse;
  border-top: 1px solid #999;
  table-layout: fixed;
  background: linear-gradient(to bottom, #fff 80%, #b40000 20%);
}

.footer-table td {
  vertical-align: middle;
  text-align: center;
  border: 1px solid #ddd;
}

.main-title,
.print-heading,
.kpi-table {
  page-break-inside: avoid;
  break-inside: avoid;
}

.print-heading + .kpi-table {
  page-break-before: avoid;
}

p, h1, h2, h3, h4, h5, h6 {
  orphans: 3;
  widows: 3;
}




/* -------- PRINT-SPECIFIC STYLING -------- */
@media print {
    
      .form {
    background: linear-gradient(145deg, #f3f3f3, #ffffff);
    border: 1px solid #999;
    padding: 15px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.3);
  }

  @page {
    size: A4;

    @bottom-right {
    content: "Page " counter(page) " of " counter(pages);
    font-size: 10pt;
    color: #999;
  }
 }
}
