css:
@import url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css');

.print-container {
  font-family: "Helvetica", Arial, sans-serif;
  padding: 24px;
  font-size: 12px;
  color: #333;
}

.section-heading {
  font-size: 16px;
  font-weight: bold;
  color: #b40000;
  border-bottom: 2px solid #b40000;
  padding-bottom: 6px;
  margin: 32px 0 14px;
}

.section-heading i {
  margin-right: 6px;
  color: #b40000;
}

.info-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 24px;
  font-size: 12px;
}

.info-table th,
.info-table td {
  border: 1px solid #ccc;
  padding: 8px 10px;
  text-align: left;
  vertical-align: top;
}

.info-table th {
  background-color: #f5f5f5;
  font-weight: bold;
  color: #000;
  width: 35%;
}

.info-table tr:nth-child(odd) td {
  background-color: #fdf2f2; /* light red zebra stripe */
}

.info-table tr:nth-child(even) td {
  background-color: #fff;
}
